import React, { Component } from 'react';
import axios from 'axios'
export default class Edit extends Component {
constructor(props){
    super(props)
    this.state = {
        karyawan : 'Loading...',
        id : null,
     }
     this.handlerChange = this.handlerChange.bind(this)
     this.onSubmit = this.onSubmit.bind(this)
}
    fetchData(){
        axios.get(`http://localhost:8000/karyawan/${this.props.match.params.id}`)
        .then(res => res.data[0])
        .then(data => {
            this.setState({
                id : data.id,
                karyawan : data.nama_karyawan,

            })
        })
    }
    componentDidMount(){
        this.fetchData()
    }

  onSubmit(e) {
    e.preventDefault();
    const { karyawan, id } = this.state
    console.log(id)
    axios({
        method: 'put',
        url: `http://localhost:8000/karyawan/${id}`,
        data: {
          nama_karyawan: karyawan,
        }
      })
      .then(res => res.data)
      .then(status => {
          alert(status)
      })
  }
  handlerChange(e){
    this.setState({
        [e.target.name] : e.target.value
    })
  }
    render() {
        console.log(this.state.data)
        return (
            <div style={{marginTop: 10}}>
                <h3>Edit Karyawan</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Nama Karyawan:  </label>
                        <input type="text"  className="form-control" name="karyawan" value={this.state.karyawan} 
                        onChange={this.handlerChange}/>
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Edit" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
